import React, { useState } from 'react';
import axios from 'axios';

function Projects() {
    const [overlayVisible, setOverlayVisible] = useState(false);
    const [resultadoCep, setResultadoCep] = useState('');

    function mostrarConsultaCep() {
        setOverlayVisible(false);
    }

    function toggleCepBox(show) {
        setOverlayVisible(show);
    }

    function buscarCep() {
        const cep = document.getElementById('cepInput').value;
        const url = `https://viacep.com.br/ws/${cep}/json/`;

        axios.get(url)
            .then(response => {
                const data = response.data;
                if (data.erro) {
                    setResultadoCep('CEP não encontrado.');
                } else {
                    setResultadoCep(
                        `Endereço: ${data.logradouro}<br/>
                        Bairro: ${data.bairro}<br/>
                        Cidade: ${data.localidade}<br/>
                        Estado: ${data.uf}`
                    );
                }
            })
            .catch(error => {
                setResultadoCep('Erro ao buscar o CEP.');
            });
    }

    return (
        <div className="projects" id="projects">
            <h2 className="heading">Latest<span> Projects</span></h2>

            <div className="projects-content">
                {/* Other projects */}
                <div className="projects-box">
                    <div className="cep">
                        <div className="cep-cover" style={{ display: overlayVisible ? 'none' : 'flex' }}>
                            <button onClick={() => {
                                toggleCepBox(true);
                                mostrarConsultaCep();
                            }}>Visualize</button>
                        </div>

                        <h4>CEP Search</h4>
                        <p>Enter a ZIP code to view its location and information:</p>
                        <input type="text" id="cepInput" placeholder="Digite um CEP" />
                        <button onClick={() => {
                            toggleCepBox(true);
                            buscarCep();
                        }}>Search</button>
                        <div id="resultadoCep" dangerouslySetInnerHTML={{ __html: resultadoCep }}></div>
                        <button onClick={() => toggleCepBox(false)} id="closeButton" style={{ display: overlayVisible ? 'block' : 'none' }}>Exit</button>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default Projects;
